function beautifyCode(id){
	var code = btoa(id);
	var result = "";

	var phase = 4;
	var len = 5;
	for (i = 0; i < phase; i++)
	{
		start = (i * len) + 1;
		result += (code.substr(start, len));
		if (i < (phase - 1))
		{
			result += ("-");
		}
	}
	return result.toLowerCase();
};

